import addToSemiInfiniteLoop from './semiInfiniteLoop';

/**
 * @private
 * @param {Object} tickEntry
 * @return {void}
 */
function checkError(tickEntry){
	if(!tickEntry instanceof TickEntry){
		const className = tickEntry.constructor ? tickEntry.constructor.name : typeof tickEntry;
		throw new Error(`Ticker: Expecting instance of TickEntry got ${className}`);
	}
	if(!tickEntry.func){
		throw new Error("Ticker: function can't be undefined");
	}
}
/**
 * Tick Entry is a wrapper to the function we want to execute later in request animation frame or event cycle
 * @example
 * import Ticker from 'ticker'
 * let tickEntry = new TickEntry(context,listener,callback,priority);
 */
export default class TickEntry
{
	/**
	 * @param {Object} context The "this" argument for the listener function.
	 * @param {Function} func
	 * @param {number} priority
	 * @param {Function} callback
	 */
	constructor(context, func, priority = 0, callback = null)
	{
		/**
		 * @type {Object}
		 */
		this.context = context;
		/**
		 * @type {Function}
		 */
		this.func = func;
		/**
		 * @type {Function}
		 */
		this.callback = callback;
		/**
		 * @type {number}
		 */
		this.priority = priority;
		/**
		 * @type {number}
		 */
		this.executionCount = 0;
		checkError(this);
	}


	/**
	 * This function dispose the tickEntry references for garbage collection
	 *
	 * @return {void}
	 */
	dispose(){
		this.context = null;
		this.func = null;
		this.callback = null;
		this.priority = null;
		this.executionCount = NaN;
	}

	/**
	 * This function adds the func to event cycle / request animation frame for execution
	 *
	 * @return {void}
	 */
	executeInCycle(){
		checkError(this);
		addToSemiInfiniteLoop(this);
	};

	/**
	 * This function adds the loop function to event cycle / request animation frame for execution
	 *
	 * @param {number} maxLoopCount indicates loop length that needs to be executed.
	 * @param {number} endIndex indicates loop end value
	 * @param {number} startIndex indicates loop begining value
	 * @return {void}
	 */
	executeAsSmallLoopsInCycle(maxLoopCount, endIndex, startIndex = 0){
		checkError(this);
		let loopLimit = maxLoopCount;
		let i = startIndex;
		let allowedTickCount = TickEntry.allowedTickCount;
		TickEntry.allowedTickCount = undefined;

		let loopFunction = this.func;
		let loopFnContext = this.context;
		let loopFnCallback = this.callback;

		this.context = this;
		if(loopFnCallback){
			this.callback = function() {
				loopFnCallback.call(loopFnContext || loopFnCallback['this'], i)
			};
		}

		this.func = function(){
			if(i < loopLimit) {
				loopFunction.call(loopFnContext || loopFunction['this'], i);
				i = i + 1;
				this.executeInCycle();
			} else if(loopLimit < endIndex){
				loopLimit = loopLimit + maxLoopCount;
				this.executeInCycle()
			} else {
				this.dispose();
				loopFunction = null;
				loopFnContext = null;
				TickEntry.allowedTickCount = allowedTickCount;
			}
		};

		this.executeInCycle();
	};

}

TickEntry.HIGH = 0;
TickEntry.NORMAL = 1;
TickEntry.LOW = 2;

TickEntry.allowedTickCount = 100;

import TickEntry from './TickEntry';

let tickID = 0;// for Windows Env
//[0-HIGH, 1-NORMAL, 2-LOW]
let priorityEntries = [null, null, null];
let waitEntries = null;

let tickCount = 0;
let isExecuting = false;

/**
 * @private (Core algorithm)
 * caller: Animation Frame | event cycle Callback
 *
 * Executes entries in animation frame / event cycle callback
 * First execute the entries in the priority order
 * Entries which are added to Wait queue while executing priority entries are moved for execution
 * If there are no entries in priority, semi-infinite loop is stopped
 * and return boolean flag to remove the callback from animation frame or event cycle.
 *
 * @return {boolean} notifies whether to continue or remove the callback
 */
function onTick(){
	tickCount++;

	if(TickEntry.allowedTickCount !== undefined){
		if(tickCount < TickEntry.allowedTickCount){
			return coreAlogorithm();
		 } else {
			console.warn("Animation frame loop executed to its set limit: ", TickEntry.allowedTickCount);
			stopSemiInfiniteLoop();
			return false;
		}
	} else {
		return coreAlogorithm();
	}
	return true;
}

function coreAlogorithm(){
	executePriorityEntries();
	moveWaitingEntriesForExecution();
	if(arePriorityEntriesEmpty()){
		stopSemiInfiniteLoop();
		return false;
	}
	return true;
}

/**
 * @private
 * function that executes the entries in each priority
 *
 * @return {void}
 */
function executePriorityEntries(){
	isExecuting = true;
	for(let index = 0 ; index < priorityEntries.length; index++){
		let tickEntries = priorityEntries[index];
		if(tickEntries && tickEntries.length > 0) {
			executeTickEntries(tickEntries);
			//Clear them once executed
			priorityEntries[index] = null;
		}
	}
	isExecuting = false;
}

/**
 * @private
 * function that executes all the entries in particular priority
 *
 * @return {void}
 */
function executeTickEntries(tickEntries){
	// important to use for-loop
	// tickEntries grows dynamically by one of its entry
	// for example: let say we have one entry, and executing that entry might adds another entry
	// with map function we cant execute dynamically growing entries.
	for(let i = 0; i < tickEntries.length; i++){
		const tickEntry = tickEntries[i];
		tickEntry.func.call(tickEntry.context || tickEntry.func['this']);

		if (tickEntry.callback) {
			tickEntry.callback.call(tickEntry.context || tickEntry.callback['this']);
		}
		tickEntry.executionCount++;
	}
}

function moveWaitingEntriesForExecution(){
	const entriesCount = waitEntries ?  waitEntries.length : 0;
	if(waitEntries && entriesCount > 0) {
		for(let index = 0 ; index < entriesCount; index++){
			let tickEntry = waitEntries[index];
			const { priority } = tickEntry;
			if(!priorityEntries[priority]){
				priorityEntries[priority] = [];
			}
			const tickEntries = priorityEntries[priority];
			tickEntries.push(tickEntry);
		}
	}
	waitEntries = null;
}

function arePriorityEntriesEmpty(){
	for(let index = 0 ; index < priorityEntries.length; index++){
		let tickEntries = priorityEntries[index];
		if(tickEntries && tickEntries.length > 0) {
			return false
		}
	}
	return true;
}

/**
 * @private
 * Animation frame callback function which execute the entries
 *
 * @return {void}
 */
function tickCallback(){
	const shouldContinue = onTick();
	if(shouldContinue){
		tickID = window.requestAnimationFrame(tickCallback);
	}
}

/**
 * @private
 * Starts the Semi-infinite loop to execute the functions
 * Executed when first entry is added.
 *
 * @return {void}
 */
function startSemiInfiniteLoop() {
	if(window){
		// will receives timestamp as argument
		tickID = window.requestAnimationFrame(tickCallback);
	} else{
		tickID = setTimeout(tickCallback);
	}
}

/**
 * @private
 * Stops the Semi-infinite loop.
 * Executed after executing all the entries both in priority and wait
 *
 * @return {void}
 */
function stopSemiInfiniteLoop() {
	tickCount = 0;
	isExecuting = false;
	priorityEntries = [null, null, null];
	waitEntries = null;
	if(window){
		window.cancelAnimationFrame(tickID);
	} else {
		clearTimeout(tickID);
	}
};

/**
 * @private
 * only visible to TickEntry and not accessible
 * adds the tickEntry to queue in priority order
 * if entries are empty starts the semi-infinite loop in event cycle or Animation frame
 * if execution of entries are in progress, the entry is added to waiting queue
 * thus functions are executed in the order they are added to the semi-infinite loop
 * @param {Object} tickEntry
 * @return {void}
 */
export default function addToSemiInfiniteLoop(tickEntry) {
	if(arePriorityEntriesEmpty()){
		startSemiInfiniteLoop()
	}
	if(isExecuting){
		if(!waitEntries){
			waitEntries = [];
		}
		waitEntries.push(tickEntry);
	} else {
		const { priority } = tickEntry;
		if(!priorityEntries[priority]){
			priorityEntries[priority] = [];
		}
		const tickEntries = priorityEntries[priority];
		tickEntries.push(tickEntry);
	}

};

# Ticker
[![Build Status](https://travis-ci.com/StateTree/ticker.svg?branch=master)](https://travis-ci.com/StateTree/ticker)
### What?
* Executes Functions later in animation frame / event cycle callback
* call later: wrapper to request animation frame callback or event cycle, perks are being adding priorty order

### Why?
* Cost effective Function can be broken into smaller functions and executed later
* Loops can be broken down to smaller loops with out holding execution resources.

### How?

##### executeInCycle

```
import Ticker from '@statetree/ticker';

// function to execute later
function callMeLater() {
    return "called later";
}

// excutes right after call me later function (optional)
function callback() {
    // consumer of this library can decide what they want to do
    ticker2.dispose();
}

// create the ticker object
var ticker1 = new Ticker(this, callMeLater, 1);
var ticker2 = new Ticker(this, callMeLater, 0 , callback);

// add to execute later in animation frame callback or Event cycle callback
ticker1.executeInCycle()
```

##### executeAsSmallLoopsInCycle

```
import Ticker from '@statetree/ticker';

const array = [];
for(let i= 0 ; i < 100; i++){
	array[i] = i;
};

function forLoopCode (index){
	console.log("forLoopCode: ", array[index]);
}

function forLoopCodeCallback(executedIndex){
	console.log("Executed Index: ", executedIndex);
}

let loopTicker = new Ticker(window, forLoopCode, 0, forLoopCodeCallback);
loopTicker.executeAsSmallLoopsInCycle(10, 100);
```

##### dispose

```
import Ticker from '@statetree/ticker';

// function to execute later
function callMeLater() {
    return "called later";
}

function callback() {
    ticker.dispose();
}

// create the ticker object
var ticker = new Ticker(this, callMeLater, 0 , callback);

// add to execute later in animation frame callback or Event cycle callback
ticker.executeInCycle()
```

### Core Algorithm
 * Entry added to priority Stack
    1. If first Entry starts the semi-infinite loop
    2. if priority order stack entries are executing, entry added to wait stack
 * Executes the entries in the priority order
 * Added wait entries are moved to priority entries for execution
 * If there are no entries in priority, semi-infinite loop is stopped
 * and return boolean flag to remove the callback from animation frame or event cycle.

