/*
* Chainable Class
* */
export default class Notifier {
	constructor(){
		this.progressCallback;
		this.doneCallback;
		this.errorCallback;
	}
}

Notifier.prototype.onError = function(func){
	this.errorCallback = func;
	return this;
}

Notifier.prototype.onProgress = function (func){
	this.progressCallback = func;
	return this;
}

Notifier.prototype.onDone = function (func){
	this.doneCallback = func;
	return this;
}

import {addToSemiInfiniteLoop} from './scheduler';
import Notifier from './Notifier';
import {ErrorMsg} from './contants';

/**
 * @private
 * @param {Object} tickEntry
 * @return {void}
 */
function _checkError(tickEntry){
	if(!tickEntry){
		throw new Error('Ticker: instance can\'t be null');
	} else if(!(tickEntry instanceof TickEntry)){
		const className = tickEntry.constructor ? tickEntry.constructor.name : typeof tickEntry;
		throw new Error(`Ticker: Expecting instance of TickEntry got ${className}`);
	} else if(!tickEntry.func){
		throw new Error('Ticker: function can\'t be undefined');
	}
}


/**
 * Tick Entry is a wrapper to the function we want to execute later in request animation frame or event cycle
 * @example
 * import Ticker from 'ticker'
 * let tickEntry = new TickEntry(context,listener,callback,priority);
 */
export default class TickEntry
{
	/**
	 * @param {Function} func
	 * @param {Object} context The "this" argument for the func.
	 * @param {number} priority
	 */
	constructor(func, context = null, priority = 0)
	{
		/**
		 * @type {Object}
		 */
		this.context = context;
		/**
		 * @type {Function}
		 */
		this.func = func;
		/**
		 * @type {number}
		 */
		this.priority = priority;
		/**
		 * @type {number}
		 */
		this.executionCount = 0;
		this.notifier = new Notifier();
		_checkError(this);
	}

	onDone(doneCallback){
		this.notifier.doneCallback = doneCallback;
		return this.notifier;
	}

	onError(errorCallback){
		this.notifier.errorCallback = errorCallback;
		return this.notifier;
	}


	/**
	 * This function dispose the tickEntry references for garbage collection
	 *
	 * @return {void}
	 */
	dispose(){
		this.context = null;
		this.func = null;
		this.priority = null;
		this.executionCount = NaN;
		this.notifier = null;
	}

	/**
	 * This function adds the func to event cycle / request animation frame for execution
	 *
	 * @return {void}
	 */
	executeInCycle(){
		_checkError(this);

		const tickEntryInstance = this;
		const {func, context, priority} = tickEntryInstance;

		function loopFunction(){
			const notifier = tickEntryInstance.notifier;
			const {doneCallback, errorCallback} = notifier;
			try{
				const result = func.call(context);
				tickEntryInstance.executionCount++;
				doneCallback && doneCallback(result);
			} catch (error){
				if(errorCallback){
					errorCallback(error);
				} else {
					console.log(error)
				}
				tickEntryInstance.dispose();
			}
		}

		addToSemiInfiniteLoop(loopFunction,priority);

		this.notifier.onProgress = undefined;
		return this.notifier;
	}

	/**
	 * This function adds the loop function to event cycle / request animation frame for execution
	 *
	 * @param {number} maxLoopPerFrame indicates loop length that needs to be executed in single frame or tick.
	 * @param {number} endIndex indicates loop end value
	 * @param {number} startIndex indicates loop begining value , default value is 0
	 * @return {void}
	 */
	executeAsSmallLoopsInCycle(maxLoopPerFrame, endIndex, startIndex = 0){
		if(maxLoopPerFrame === undefined || typeof maxLoopPerFrame !== 'number'){
			throw new Error(ErrorMsg.MAX_LOOP_PER_FRAME);
		}

		if(endIndex === undefined || typeof endIndex !== 'number'){
			throw new Error(ErrorMsg.END_INDEX);
		}

		if(typeof startIndex !== 'number'){
			throw new Error(ErrorMsg.START_INDEX);
		}
		_checkError(this);

		const tickEntryInstance = this;
		const {func, context, priority} = tickEntryInstance;

		let loopLimit = maxLoopPerFrame;
		let i = startIndex;

		function loopFunction(){
			const notifier = tickEntryInstance.notifier;
			const {doneCallback, errorCallback, progressCallback} = notifier;
			let result;
			for(;i < loopLimit; i++){
				try {
					result = func.call(context, i);
				} catch(error) {
					errorCallback && errorCallback(error);
					tickEntryInstance.dispose();
					return;
				}
			}
			if(loopLimit < endIndex){
				loopLimit = loopLimit + maxLoopPerFrame;
				progressCallback && progressCallback(i, result);
				addToSemiInfiniteLoop(loopFunction,priority);
			} else if( i === endIndex){
				tickEntryInstance.executionCount++;
				doneCallback && doneCallback(result);
			}
		}

		addToSemiInfiniteLoop(loopFunction, priority);
		return this.notifier;
	}

}

TickEntry.HIGH = 0;
TickEntry.NORMAL = 1;
TickEntry.LOW = 2;

TickEntry.allowedTickCount = 100;

export const ErrorMsg = {
	MAX_LOOP_PER_FRAME: 'Maximum Loop Per Frame has to be a number',
	START_INDEX: 'Start Index has to be a number',
	END_INDEX: 'End Index has to be a number'
};

function Memory(){
	//[0-HIGH, 1-NORMAL, 2-LOW]
	let priorityEntries = [null, null, null];
	let waitEntries = null;
	let isBusy = false;

	function reset(){
		isBusy = false;
		priorityEntries = [null, null, null];
		waitEntries = [];
	}

	function isMemoryEmpty() {
		const levels = priorityEntries.length;
		for (let i = 0; i < levels; i++) {
			let entries = priorityEntries[i];
			if (entries && entries.length > 0) {
				return false;
			}
		}
		return true;
	}

	function addToPrimary(func, level){
		if(!priorityEntries[level]){
			priorityEntries[level] = [];
		}
		const functions = priorityEntries[level];
		functions.push(func);
	}

	function addToSecondary(func, level){
		if(!waitEntries){
			waitEntries = [];
		}
		waitEntries.push({
			func,
			level
		});
	}

	function readInLevel(execute,level){
		let entries = priorityEntries[level];
		if(entries && entries.length > 0) {
			execute(entries);
			//Clear them once executed
			priorityEntries[level] = null;
		}
	}


	function read(execute){
		const levels = priorityEntries.length;
		for(let level = 0 ; level < levels; level++){
			readInLevel(execute,level);
		}
	}

	function move(){
		const entriesCount = waitEntries ? waitEntries.length : 0;
		if(entriesCount > 0) {
			for(let index = 0 ; index < entriesCount; index++){
				let waitEntry = waitEntries[index];
				const {func, level} = waitEntry;
				addToPrimary(func, level);
			}
		}
		waitEntries = null;
		return entriesCount == 0;

	}

	function readAndExecute(execute){
		isBusy = true;
		read(execute); // execute and wipes primary memory
		isBusy = false;
		return move(); // move from sec to pri for next round and returns false, if entries are left in pri after move
	}

	function add(func,level){
		if(isBusy){
			addToSecondary(func, level);
		} else {
			addToPrimary(func, level);
		}
	}

	return {
		readAndExecute,
		reset,
		add,
		isMemoryEmpty
	}
};

// Singleton Instance
const memory = Memory();

export default memory;

import memory from './memory';


function scheduler(maxLoop){
	let tickID = 0;// for Browser Env
	let tickCount = 0; // to avoid infinite loop issue, we need to count
	let allowedTickCount = maxLoop ? maxLoop : 100;

	function executeEntries(funcs){
		// important to use for-loop
		// tickEntries grows dynamically by one of its entry
		// for example: let say we have one entry, and executing that entry might adds another entry
		// with map function we cant execute dynamically growing entries.
		for(let i = 0; i < funcs.length; i++){
			const func = funcs[i];
			func.call(func['this']);
		}
	}


	/**
	* @private (Core algorithm)
  * caller: Animation Frame | event cycle Callback
	*
	* Executes entries in animation frame / event cycle callback
	* First execute the entries in the priority order
	* Entries which are added to Wait queue while executing priority entries are moved for execution in next animation frame
  * If there are no entries in priority, semi-infinite loop is stopped
	*
	* @return {void}
	*/
	function tickCallback(){
		tickCount++;
		if(tickCount < allowedTickCount){
			const isCompleted = memory.readAndExecute(executeEntries);
			if(!isCompleted){
				start() // will execute in next animation Frame
			} else {
				stop()
			}
		} else {
			console.warn('Animation frame loop executed to its set limit: ', allowedTickCount);
			stop()
		}
	}

	/**
	* @private
	* Starts the Semi-infinite loop to execute the functions
	* get started when first entry is added.
	*
	* @return {void}
	*/
	function start() {
		if(window){
			// will receives timestamp as argument
			tickID = window.requestAnimationFrame(tickCallback);
		} else{
			tickID = setTimeout(tickCallback);
		}
	}

	/**
	* @private
	* Stops the Semi-infinite loop.
	* Get stopped after executing all the entries both in priority and wait
	* Also get stopped when executed more in animation loop
	* @return {void}
	*/
	function stop() {
		tickCount = 0;
		memory.reset();
		if(window){
			window.cancelAnimationFrame(tickID);
		} else {
			clearTimeout(tickID);
		}
	}

	/**
	* @private
	* only visible to TickEntry and not accessible
	* adds the tickEntry to queue in priority order
	* if entries are empty starts the semi-infinite loop in event cycle or Animation frame
	* if execution of entries are in progress, the entry is added to waiting queue
	* thus functions are executed in the order they are added to the semi-infinite loop
	* @param {Function} func
	* @param {number} level
	* @return {void}
	*/
	return function (func, level) {
		if(memory.isMemoryEmpty()){
			start();
		}
		memory.add(func, level);
	}
}

export const addToSemiInfiniteLoop = scheduler();

